declare module "*.svg" {
	const content: SVGRectElement | SVGRect | string
	export default content
}

declare module "*.gif" {
	const content: string
	export default content
}

declare module "*.png" {
	const content: string
	export default content
}

declare module "*.jpeg" {
	const content: string
	export default content
}

declare module "*.mdx" {
	const content: string
	export default content
}

declare module "*.md" {
	const content: string
	export default content
}
